CREATE DEFINER=`root`@`%` FUNCTION `fun_getshareparent`(
#获取分享的层级记录,最多返回三级分享的用户
	userid	char(36),
	#shareid char(36),
    parentLevel int
) RETURNS varchar(255) CHARSET utf8
BEGIN
	declare shareid char(36);
    declare parentUser char(36) default '';
    declare returnParent varchar(255) default '';

    DECLARE parentId char(36);
    DECLARE currentId char(36);
    
    #获取用户的第一级上限
    select parent_id,share_id into parentUser,shareid from crm_user where user_id=userid;
    #如果用户是自己跑进来的，而且也没有上线，则不分佣
    if parentUser is null and shareid is null then
		return '';
    end if;
    #如果用户尚未绑定第一个上限，则通过用户该订单的分享id,来设置她的上限
	if parentUser is null then
		SELECT pf_share.user_id 
        into parentUser 
        from pf_share 
        inner join crm_user  u on pf_share.user_id = u.user_id
		where share_id=shareid and pf_share.user_id<>userid;
        
        #同时设置该上级为他的永久上线
        update crm_user set parent_id = parentUser where user_id=userid;
    end if;
    #如果上级的分享者没有或是用户自己，也不分佣
    if parentUser is null or parentUser=userid then
		return '';
    end if;
    
    set returnParent = concat(returnParent,',',parentUser); 
	#获取用户的第二级上限
    if parentLevel>=2 then
		SET currentId =parentUser; 
        set parentUser = null;
		select parent_id into parentUser from crm_user where user_id=currentId;
        #如果第二级没有了，则返回
        if parentUser is null then
			return returnParent;
        end if;
        set returnParent = concat(returnParent,',',parentUser); 
    end if;
	
	#获取用户的第三级上限
    if parentLevel>=3 then
		SET currentId =parentUser; 
        set parentUser = null;
		select parent_id into parentUser from crm_user where user_id=currentId;
        #如果第二级没有了，则返回
        if parentUser is null then
			return returnParent;
        end if;
        set returnParent = concat(returnParent,',',parentUser); 
    end if;
	RETURN returnParent; 
END ;;
