CREATE DEFINER=`root`@`%` FUNCTION `FUN_JOIN2PRICE`(
p1 decimal(8,2),
p2 decimal(8,2),
discount tinyint
) RETURNS varchar(64) CHARSET utf8mb4
BEGIN
	declare retValue varchar(64);
	if ifnull(discount,100)=100 then
		set retValue = if(p1 = ifnull(p2,p1),p1,concat(p1,' ~ ',p2));
	else
		set retValue = if(p1 = ifnull(p2,p1),
						truncate(p1 * discount / 100,2),
                        concat(truncate(p1 * discount / 100,2),' ~ ',truncate(p2 * discount / 100,2)));
	end if;
	return retValue;
END ;;
