CREATE DEFINER=`root`@`%` PROCEDURE `proc_app_dataanalysis`(
#商城总体数据分析(后台首页数据）
merchantappid varchar(20),
finance	int  #是否计算财务数据
)
BEGIN
	/****统计用户数据******/
	select count(1) 					as usercount,		 #平台总用户量
    count(if(isnew=1,true,null)) 		as todayuser,       #今日新增用户
    count(if(ordercount>=1,true,null)) 	as consumeuser,		 #平台的消费用户
	ifnull((select count(1) from pf_share),0) as sharecount	#平台的总分享数		
    from vw_users where appid=merchantappid;
    
    /****统计物流数据 *****/
    
    select count(if(log_no is not null,true,null)) as logcount ,
		   count(if(log_no is null,true,null)) as readyforlog 
    from bus_order 
    
    where 
		app_id=merchantappid 
        and order_type=1 and order_isservice=0
        and order_status>=1
		and refund_status=0;
        #and log_no is not null  #总的物流订单
    
    
    if finance = 1 then
		/****统计财务数据******/
		select 
			count(1) 						as ordercount,		#总订单量
			sum(paytotal)					as ordertotal,		#总订单金额
			sum(rebatetotal)				as rebatetotal,		#总返利金额
			
			count(if((`type`=1 or `type`=99 or `type`=100),true,null))	as normalcount,		#普通订单总数
			count(if(`type`=2,true,null))	as groupcount,		#团购订单总数
			count(if(`type`=4,true,null))	as speedcount,		#秒杀订单总数
			sum(if((`type`=1 or `type`=99 or `type`=100),paytotal,0))	as normaltotal,		#普通订单金额
			sum(if(`type`=2,paytotal,0))	as grouptotal,		#团购订单金额
			sum(if(`type`=4,paytotal,0))	as speedtotal,		#秒杀订单金额
			
			count(if(datediff(now(),order_date)=0,true,null)) 	as todayordercount,	 #今日总订单量
			sum(if(datediff(now(),order_date)=0,paytotal,0))		as todayordertotal,		#总订单金额
			count(if((`type`=1 or `type`=99 or `type`=100) and datediff(now(),order_date)=0,true,null))	as todaynormalcount,	#今日普通订单总数
			sum(if(datediff(now(),order_date)=0,rebatetotal,0))				as todayrebatetotal,	#今日总返利金额
			count(if(`type`=2 and datediff(now(),order_date)=0,true,null))	as todaygroupcount,		#今日团购订单总数
			count(if(`type`=4 and datediff(now(),order_date)=0,true,null))	as todayspeedcount,		#今日秒杀订单总数
			sum(if((`type`=1 or `type`=99 or `type`=100) and datediff(now(),order_date)=0,paytotal,0))	as todaynormaltotal,	#今日普通订单金额
			sum(if(`type`=2 and datediff(now(),order_date)=0,paytotal,0))	as todaygrouptotal,		#今日团购订单金额
			sum(if(`type`=4 and datediff(now(),order_date)=0,paytotal,0))	as todayspeedtotal,			#今日秒杀订单金额        
			
			#渠道引流的订单数据
			count(if(`channel` is not null,true,null))	as channelordercount,		#渠道引流订单总数
			sum(if(`channel` is not null,paytotal,0))	as channelordertotal,		#渠道引流订单金额
			count(if(`channel` is not null and datediff(now(),order_date)=0,true,null))	as todaychannelordercount,		#渠道引流订单总数
			sum(if(`channel` is not null and datediff(now(),order_date)=0,paytotal,0))	as todaychannelordertotal		#今日渠道引流订单金额
		from 
			vw_all_orders 
		where appid = merchantappid;
    
		#提现记录汇总
		select 
			sum(draw_money) as totaldraw,			#累计提现
            sum(if(datediff(now(),draw_date)=0,draw_money,0)) as todaydraw			#今日提现
		from pf_rebate_draw pd
        where app_id = merchantappid and transfer_state = 1;
    
    end if;
    
END ;;
