CREATE DEFINER=`root`@`%` PROCEDURE `proc_card_acceptgift`(
	#接收充值卡礼物
	userid	char(36),    #接收人
    recid	int			 #赠送出来的卡id
)
BEGIN
	
    declare result int default 1;
    declare cardmoney decimal;
    declare cardid char(36);
    declare newrecid	int default 0;
    
    declare senderid char(36);		#送卡的人
    declare cardname	varchar(128);	#礼卡的名称
    declare cardvalue	varchar(20);	#礼卡的面额
    #获取到赠卡的人，用户发送消息
    select user_id,card_title,card_value into senderid,cardname,cardvalue
    from crm_user_card cuc
    inner join bus_shoppingcard bsc on cuc.card_id=  bsc.card_id
    where rec_id=recid and card_status=0 limit 1;
    
    #确保该购物卡尚未被用过
    if senderid is not null then
		#为领取的用户创建一张卡记录
        insert into crm_user_card(user_id,card_id,order_id,card_value,card_status,parent_id,card_date)
        select userid,card_id,order_id,card_value,0,recid,now() from crm_user_card
        where rec_id=recid;
        
        #获取到新的卡记录id
        set newrecid = LAST_INSERT_ID();
        
        #将送出卡的原有记录置位为已赠送
        update crm_user_card set card_status = 1,use_date=now() where rec_id=recid;
		set result = 0;
        
    end if;
    select result,newrecid as cardid,senderid,cardname,cardvalue,user_name as username from crm_user where user_id=userid;
END ;;
