        insert into crm_user_card(user_id,card_id,order_id,card_value,card_status,card_date)
        select user_id,card_id,order_id,card_total,0,now() from bus_card_order where pay_tx_no = order_tx_no;
    end if;
	#如果存在更新的影响的记录
    select 0 as result;   #结果
END ;;
