CREATE DEFINER=`root`@`%` PROCEDURE `proc_mall_createorder`(
#创建订单用于支付
#准备进行订单交易
orderid		char(36),				#临时订单id
appid		varchar(20),			#商户的appid
userid 		char(36),				#当前购买的用户id
uservoucher	int,					#用户所选的优惠券
credit		int,					#用户所选择使用的积分豆
deliver		varchar(512),			#物流信息
paytype		int,					#支付的方式 0：微信支付，1：余额支付
paypassword varchar(128)			#如果是余额支付，需要余额的支付密码
)
outBusiness:BEGIN
	declare orderno	varchar(20) ;
    declare txno varchar(32);  	#订单的微信支付编号
    declare ordertype int; 		#订单类型
    declare grpid int;			#订单的团购id
    declare paytotal decimal(8,2) default 0;  #最终订单需要支付的金额
	declare	balanceresult int;		#用户余额
	declare exchangerate decimal default 0;
    declare expireminute int ;  #订单的过期分钟
    declare vouchertotal decimal(8,2) default 0;  #用户所选择的优惠券金额
    
     #如果是余额支付，需要检查余额是否足够
	if paytype=1 then
		set balanceresult = fun_checkbalancepay(userid,paytotal,paypassword);
		#if userbalance<paytotal then
		#	select 100 as result,'余额不足' as msg;
		#	leave outBusiness;
		#end if;
		if balanceresult=1 then
			select 101 as result,'余额不足' as msg;
			leave outBusiness;
		end if;
		if balanceresult=2 then
			select 102 as result,'支付密码错误' as msg;
			leave outBusiness;
		end if;
	end if;
    
    
    #用户选择了优惠券进行下单
    if uservoucher is not null and uservoucher>0 then
		select voucher_money
        into vouchertotal
        from crm_user_voucher cuv
        inner join bus_voucher  bv on cuv.voucher_id = bv.voucher_id
        where cuv.rec_id = uservoucher;
    end if;
	#防止订单编号已发给微信，所以在此重新生成一个订单编号
	
    #更改原来的订单编号
    #update ec_order set pay_tx_no=txno,pay_date=now() where order_id=orderid;
    #生成订单编号
    set orderno = fun_generate_orderno(1);
    set txno = concat('P-',unix_timestamp(now()));
    
    
    select order_type,group_id 
    into ordertype,grpid
    from bus_order_draft
    where order_id=orderid;
    
    #如果订单是团购订单，则占住团购位置 5分钟
    #if ordertype=2 and grpid>0 then
	#	insert into bus_grouplink_booked(group_id,order_id,valid_date)
	#	values(grpid,orderid,timestampadd(minute,2,now()));
	#end if;
    #获取订单过期的设置
    set expireminute = cast(fun_getappsetting(appid,'orderexpired',30) as unsigned);
    #如果设置为0，则订单过期日期为100000分钟以后
    /*if expireminute<=0 then
		set expireminute = 100000;
    end if;*/
    
     #获取积分的抵扣金额
    if credit>0 then
		set exchangerate= cast(fun_getappsetting(appid,'exchange',0) as decimal(8,2));
	end if;
    
    #将临时订单插入至主订单中
    insert into bus_order(
		order_id,
        app_id,
        user_id,
        share_id,
        channel_id,
        product_id,
        promotion_id,
        order_type,
        order_isservice,
        service_count,
        order_no,
        pay_tx_no,
        order_status,
        deliver_info,
        groupid,
        order_date,
        order_total,
        full_discount,
        credit_used,
        credit_total,
        user_voucher,
        voucher_total,
        logistics_total,
        pay_total,
        pay_valid_date)
	select
		orderid,
        appid,
        userid,
        fun_getusershareid(userid),
        fun_getusersharechannel(userid),
        product_id,
        promotion_id,
        order_type,
        order_isservice,
        service_count,
        orderno,
        txno,
        0,#if((pay_total-vouchertotal)>0,0,1),
        deliver,
        group_id,
        now(),
        order_total,
        full_discount,
        credit,
        credit * exchangerate,
        uservoucher,
        vouchertotal,
        logistics_total,
        if (order_total-ifnull(vouchertotal,0)-full_discount-ifnull((credit * exchangerate),0)>0,
			order_total-ifnull(vouchertotal,0)-full_discount-ifnull((credit * exchangerate),0),
            0)+ifnull(logistics_total,0),     #物流费用不能用于优惠券或满减减去
        timestampadd(minute,if(expireminute<=0,100000,expireminute),now())     #订单下单后必须三小时支付
	from bus_order_draft
		where order_id=orderid;
        
	#将临时的订单明细插入正式记录
    insert into bus_order_detail(order_id,product_id,sku_id,product_name,sku_memo,item_image,order_count,unit_price,exceptfull)
    select orderid,product_id,sku_id,product_name,sku_memo,item_image,order_count,unit_price,exceptfull
    from bus_order_detail_draft
    where order_id=orderid;
    
    #扣减购买商品的库存数量
    update bus_product_sku bps
    inner join bus_order_detail bod on bps.sku_id= bod.sku_id
    inner join bus_products bp on bps.product_id = bp.product_id
    set bps.sku_qty = bps.sku_qty - bod.order_count
    where bod.order_id=orderid and bp.product_isservice = 0;
    
    
    
    select pay_total into paytotal from bus_order where order_id=orderid;
    
    
    #返回订单信息，用于外部支付
	select
		0 as result,
		orderid,
        orderno,
        pay_tx_no as txno,
		order_total as ordertotal,
        pay_total as cost,
        if(paytotal<=0 or paytype=1,1,0) as orderstatus,
        groupid,
        expireminute,  	#订单的过期时间
        '商城购物' as describ
    from bus_order 
    where order_id=orderid;
    
    #如果订单的支付金额为0或者用户使用的是余额支付，
    #那么直接调用支付完成
    if  paytotal<=0 or paytype=1 then
		call proc_mall_orderpayed(txno,paytype);
    end if;
END ;;
DELIMITER ;
