CREATE DEFINER=`root`@`%` PROCEDURE `proc_mall_deleteproductspec`(
	specid int,
    out result int
)
BEGIN
	declare productid char(36);
    #启动事务保护
	DECLARE t_error INTEGER DEFAULT 0;  
	DECLARE CONTINUE HANDLER FOR SQLEXCEPTION SET t_error=1;  
    
    
    #从规格表里面选择出旧的记录对比，如果值没有发生变化
    #则不需要生成SKU
    select product_id into productid 
    from bus_specification where spec_id = specid;
    
    #启动事务保护
	START TRANSACTION;  
    # 修改规格主表记录,标记为删除
    update bus_specification set rec_isdeleted=1 where spec_id = specid;
    
    if productid is not NULL then
		call proc_mall_generatesku(productid);
    end if;
     #事务提交
	IF t_error = 1 THEN  
		rollback;  
	ELSE  
        commit;  
	END IF;  
    set result = t_error;
    select result;
END ;;
DELIMITER ;
