CREATE DEFINER=`root`@`%` PROCEDURE `proc_mall_generatesku`(productid char(36))
BEGIN

declare specCnt int default 0;   
declare specIndex int default 0;   
declare specId varchar(10) default '';
declare specSplitChar varchar(5) default '/\'-/\'';

declare combineSpecIdSql varchar(255) default 'concat(';
declare combineSpecValueSql varchar(255) default 'concat(';

declare specAlias varchar(2) default '';
declare specIndexChar varchar(50) default 'a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v,w';
DECLARE fullspec varchar(255) default '';
# 用来生成根据规格产生的skuSQL语句
DECLARE fullsql varchar(2048) default 'select * from ';
#获取到该产品的所有规格信息
	select  group_concat(spec_id) into fullspec from bus_specification 
	where product_id=productid and rec_isdeleted=0;

#获取到有多少种规格
set specCnt = fun_get_splitStringTotal(fullspec,',');   
#select 'specCnt',specCnt;
#创建临时表来生成SKU
	
	DROP TABLE IF EXISTS `tmp_product_sku`; 
	create temporary table `tmp_product_sku` (`valuekey` varchar(255) not null,`valuename` varchar(255) not null) DEFAULT CHARSET=utf8;   

	while specIndex < specCnt   
	do   
		set specIndex = specIndex + 1;   
        set specId = fun_splitString(fullspec,',',specIndex);
        set specAlias = fun_splitString(specIndexChar,',',specIndex);
        
        set combineSpecIdSql = concat(combineSpecIdSql,specAlias,'_value,',specSplitChar,',');
        set combineSpecValueSql = concat(combineSpecValueSql,specAlias,'_title,',specSplitChar,',');
        
        set fullsql = concat(fullsql,'(select value_id as ',specAlias,'_value,value_title as ',specAlias,'_title from bus_specification_values where spec_id=',specId,' and rec_isdeleted=0) ',specAlias,',');
        # insert into tmp_split(`val_`) values (fun_splitString(f_string,f_delimiter,i));   
	end while; 
    if (length(combineSpecIdSql)>7) then
		set combineSpecIdSql =replace(concat(substring(combineSpecIdSql,1,length(combineSpecIdSql)-7),')'),'/','');
        #select combineSpecIdSql;
        set combineSpecValueSql =replace( concat(substring(combineSpecValueSql,1,length(combineSpecValueSql)-7),')'),'/','');
        #select combineSpecValueSql;
        set fullsql = substring(fullsql,1,length(fullsql)-1);
        # select fullsql;
        set fullsql = concat('insert into tmp_product_sku select ',combineSpecIdSql,' as valuekey,',combineSpecValueSql,' as valuename',
							 ' from (',fullsql,') skulist');
		#select 'fullsql',fullsql;
		#拼装执行SQL
		SET @S = fullsql;
		PREPARE stmt1 FROM @S;
		EXECUTE stmt1; -- USING @a, @b;
		DEALLOCATE PREPARE stmt1;
    end if;
    
    set sql_safe_updates=0;
    update tmp_product_sku set valuekey = concat(productid,'-',valuekey);
    # 利用临时表的SKU数据，更新产品的SKU
    # 没有的则插入
    # 批量更新产品SKU的状态为删除 除去单规格
    # 单规格的sku 的 spec_key 直接=product_id
    #
    
    update bus_product_sku set rec_isdeleted=1 where product_id=productid and spec_key!=productid;
    
    # SKU已经存在的则将删除状态为0
    update bus_product_sku sku inner join tmp_product_sku tmp on sku.spec_key = tmp.valuekey
    set sku.rec_isdeleted=0,sku_memo = tmp.valuename where sku.product_id=productid;
    
    # 插入不存在的sku
    insert into bus_product_sku(product_id,spec_key,sku_memo)
    select productid,valuekey,valuename from tmp_product_sku 
    where valuekey not in (select spec_key from bus_product_sku where product_id=productid)
    order by valuekey;
    DROP TABLE IF EXISTS `tmp_product_sku`; 
	
END ;;
