CREATE DEFINER=`root`@`%` PROCEDURE `proc_mall_getgroupbuyprogress`(
#查看当前的团购进度情况
userid	char(36),	#当前查看此团购信息的用户
groupid int,
recommend int #是否显示推荐商品
)
BEGIN
	declare productid char(36);  #当前拼团中的商品
    
    declare grouptext varchar(128) default '邀请好友一起团,满团即享';
    
    select product_id into productid from bus_grouplink  
    where group_id = groupid;
    
	
	#团购的信息
	select
		groupid
		,group_max 		as group_max
		,group_current 	as group_current
        ,timestampdiff(second,now(),group_enddate)  as groupend
        #,if(group_max>group_current,0,1) as groupsuccessed
        ,group_successed as groupsuccessed
        ,group_pricetype	as pricetype
        ,group_price		as price
        ,pd.id		as productid
        ,pd.cover			as cover
		,pd.promotionlimittype
        ,pd.promotionlimit
        ,pd.promotionsellcount
        ,pd.promotionsellout
        ,(select order_id from bus_order where bus_order.groupid=groupid and user_id=userid limit 1) as userjoined
        ,
		(
		case group_pricetype 
			when 0 then  concat_ws('',grouptext,'团购一口价',group_price)
			when 1 then  concat_ws('',grouptext,'全单',if(group_price % 10=0,floor(group_price/10),truncate(group_price,0)) ,'折优惠')
		 end
		) as grouptext
    from bus_grouplink grp
    inner join vw_product pd on grp.product_id= pd.id
    where group_id = groupid;
    
    #参与的拼团人员
    select 
		cu.user_name		as username
        ,cu.user_logo		as userlogo
    from bus_order
    inner join crm_user cu on bus_order.user_id = cu.user_id
    where bus_order.groupid=groupid and order_status>=1
    order by order_date;
    
    
    #选出其他几条参团信息供选择
    select
		group_id 					as groupid,
		group_current 				as groupcurrent,
		(group_max-group_current) 	as groupremain,
		group_enddate				as groupend,
		bo.usernames,
		bo.userlogos
	from bus_grouplink link
	inner join vw_grouporder bo on link.group_id=bo.groupid
	where  
		product_id=productid
        and group_successed=0
        and timestampdiff(second,now(),group_enddate)<0
		and group_current<group_max
	order by group_current desc
	limit 2;
    
    #显示产品的相关推荐
    if recommend =1 then
		select 
				pd.id,
				pd.title,
				pd.inpromotion,		# 商品是否促销
				pd.promotion,		# 商品当前的促销模式（1，正常  2：团购  4：秒杀   8：预售）
                pd.cover,
				truncate(pd.lowprice,2) as cost,
				pd.describ,
				pd.lowprice,
				pd.maxprice,
				pd.buycount			as salecount  #当前总销量
		from bus_product_recommend  rec 
        inner join bus_grouplink grp on grp.product_id = rec.product_id
		inner join vw_product pd on rec.recommend_product=pd.id
		where  grp.group_id=groupid;
    end if;
END ;;
