    from bus_order
    where order_no=orderno;
    
    #订单记录没有找到
    if orderid is null then
		select 100 as result,'无此订单' as msg;
        leave business;
    end if;
    
    if paystatus >0 then
		select 1 as result,'订单已支付' as msg;
        leave business;
	end if;
    
    #已经过期
    if timestampdiff(MINUTE,expiredate,now())>0 then
		select 2 as result,'订单支付已过期' as msg;
        LEAVE business;
    end if;
    
      #如果是余额支付，需要检查余额是否足够
	if paytype=1 then
		set balanceresult = fun_checkbalancepay(userid,paytotal,paypassword);
		if balanceresult=1 then
			select 101 as result,'余额不足' as msg;
			leave business;
		end if;
		if balanceresult=2 then
			select 102 as result,'支付密码错误' as msg;
			leave business;
		end if;
	end if;
    
    
     #重新刷新微信支付的交易订单号
    set newTxNo = concat('P-',unix_timestamp(now()));
	
   
	update bus_order
		set 
        deliver_info	= if(ifnull(deliver,'')='',deliver_info,deliver),
        pay_tx_no 		= newTxNo,
		pay_date 		= now()
	where order_id=orderid;
	#进行拼团的计算
	#call proc_ec_order4groupbuy(orderid);
    #进行返利的计算
    
     #返回订单信息，用于外部支付
	select 
		0 as result,
        'ready pay' as msg,
		orderid,
        orderno,
        newTxNo as txno,
		ordertotal,
        paytotal as cost,
        if(paytotal<=0 or paytype=1,1,0) as orderstatus,
        groupid,
        '商城购物' as describ;
    #rebateUser1,rebateUser2,result,ordertype;
    
    #如果订单的支付金额为0或者用户使用的是余额支付，
    #那么直接调用支付完成
    if  paytotal<=0 or paytype=1 then
		call proc_mall_orderpayed(newTxNo,paytype);
    end if;
END ;;
