CREATE DEFINER=`root`@`%` PROCEDURE `proc_mall_getproductdetail`(
	productid char(36),
    userid char(36),		#当前进入的用户
    recommend tinyint		#是否获取推荐的商品
)
BEGIN
	#当前商品是否正在限时打折
	declare indiscount int default 0;
    #折扣率是多少
    declare discount decimal(8,2) default 100.0;
    declare appid varchar(20);
    declare groupid int default 0;
    declare promotion int;
    declare inpromotion int;
    declare pricetype int default 1; 
    declare realprice decimal(8,2) default 100.0;
    declare catalogid char(36);
    declare groupmax int;
    declare groupcount int;
    declare groupissuccessed int;
    declare grouprefund int;
    declare groupvalid datetime;
    declare fulldescrib varchar(255); #满减描述 
    
    #获取到产品的部分id来判断满减
    select app_id,catalog_id into appid,catalogid
    from bus_products where product_id = productid;
    
    if not exists(
		select 1 from bus_full_exception 
        where  app_id=appid and (
			(data_type = 0 and data_id = catalogid) or
            (data_type = 1 and data_id = productid))) then

			select 
				group_concat(full_name order by full_order_total separator ',')
			into fulldescrib
            from bus_full_promotion
            where full_enabled = 1;
	end if;
    #select user_member_level into member_level from crm_user where user_id=userid;
	#产品信息
    DROP TABLE IF EXISTS `tmp_product`; 
    create TEMPORARY table tmp_product 
    (
		select 
        app_id,
        id,
		title, #pro.product_name,
		video, #pro.product_video,
        cover as image,
        concat_ws(',',cover,cover1,cover2,cover3) as cover,
		describ,#product_describ,
        fulldescrib,
        label,  #product_label,
        tag,
        cost,	#商品的价格区间
		pro.indiscount,
        pro.discount,
        FUN_JOIN2PRICE(discount_low,discount_max,NULL) AS discountcost,
        pro.inpromotion,
        pro.promotion,
        pro.groupmin,
        pro.groupminute,
        pro.isservice,
        #商品可供购买的方式
		/*
			1（0x0001):最普通方式购买（原价购买）
			2:(0x0010):可以团购
			3:(0x0011):即可普通买又可以团购
			4:(0x0100):秒杀购买
		*/
        (case 
			when pro.promotion=2 and pro.inpromotion=1 then 3
            when pro.promotion=4 and pro.inpromotion=1 then 4
            when pro.promotion=8 and pro.inpromotion=0 then 0    #预售
            else 1 end) as buytype,
		promotionstart,
        promotionend,
        promotionstartdistance,
        promotionenddistance,
        promotionpricetype,
        promotionprice,
        promotionsellout,
        promotionsellcount,
        promotionlimit,
        promotionlimittype,
        FORMAT(promotion_low,2) as promotionlowprice ,
        FUN_JOIN2PRICE(promotion_low,promotion_max,NULL) AS promotioncost,
        sharerebate,
        spec,
        #FUN_JOIN2PRICE(lowprice,maxprice,NULL) AS cost,
		FORMAT(lowprice,2) as lowprice,
		maxprice as highprice,
        buycount
		from vw_product  pro
		where  pro.id=productid
    );
	
    #获得价格因素,用于反应到sku 的单价上
	select
		tmp_product.indiscount,
        tmp_product.discount,
		tmp_product.inpromotion,
		tmp_product.promotion,
        promotionpricetype,
        promotionprice,
        app_id
	into indiscount,discount,inpromotion,promotion,pricetype,realprice,appid
	from tmp_product limit 1;
    
    #如果商品开启了限时折扣，SKU的价格用折扣的参数
    if indiscount=1 then
		set pricetype = 1;
        set realprice = discount;
    end if;
    #当前是否开启团购促销 检查团购信息
    if promotion=2 and inpromotion=1 then
		#检查用户是否当前是被别人带入到此产品详情中
        set groupid = cast(ifnull(fun_getshareparameter(userid,'groupid'),0) as signed);
        #检查该团购是否已经满员或者已经过期
        if groupid>0 then
			#检查当前团购的人数是否已满或者团购是否已过期
			select group_id,group_max,ifnull(group_current,0),group_enddate,group_successed,group_refund
					into groupid,groupmax,groupcount,groupvalid,groupissuccessed,grouprefund
					from bus_grouplink 
                    where group_id=groupid 
						  and product_id=productid
                          and group_max>group_current
						  and group_refund=0 
						  and timestampdiff(minute,group_enddate,now())<0;
                    
			if groupid is not null then
				#该团购链已被设置为成功或者已退款，则不能再进团或者已超过时间
				#预参团的订单数量
				select groupcount+ifnull(count(1),0) 
					into groupcount
					from bus_grouplink_booked 
					where group_id=groupid and 
						timestampdiff(minute,valid_date,now())<0;
				if groupcount>groupmax or datediff(now(),groupvalid)>0 then
					set groupid = 0;
				end if;
			else
				set groupid = 0;
			end if;
        end if;
    end if;
    
    /*以下信息为返回信息*/
	#商品信息
	select groupid,tmp_product.* from tmp_product;
   
    #SKU信息
    select 
			sku_id,
			spec_key as `key`,
			spec_key,
            ifnull(sku_price,0) as sku_price,
            if (pricetype=0,realprice,ifnull(sku_price,0)  *realprice / 100) as promotion_price ,
			if(ifnull(sku.sku_image,'')='',pro.image,sku.sku_image) as image,
			sku_qty,
			sku_memo
	from tmp_product pro 
	inner join bus_product_sku sku on  pro.id=sku.product_id 
	where ifnull(sku.sku_qty,0)>0 and sku.rec_isdeleted=0;
	
	# 查询出规格 前端去解析
	select spec_id as id ,spec_name as name from bus_specification 
	where rec_isdeleted=0 and product_id=productid;
    
	select s.spec_id,value_id,value_title from bus_specification  s
	inner join bus_specification_values v on s.spec_id=v.spec_id 
    where  s.product_id=productid and s.rec_isdeleted=0 and v.rec_isdeleted=0;
    
    #如果商品能够团购,选出团购链接者的信息
    if promotion=2 and inpromotion=1 then
		if groupid>0 then
			select
				group_id 					as groupid,
                group_current 				as groupcurrent,
                (group_max-group_current) 	as groupremain,
				group_enddate				as groupend,
                usernames,
                userlogos
            from bus_grouplink link
            left join vw_grouporder bo on link.group_id=bo.groupid
            where  group_id=groupid;
		else   #选出最近的三条他人的组团信息给用户选择加团
			select
				group_id 					as groupid,
                group_current 				as groupcurrent,
                (group_max-group_current) 	as groupremain,
                group_enddate				as groupend,
                bo.usernames,
                bo.userlogos
            from bus_grouplink link
            inner join vw_grouporder bo on link.group_id=bo.groupid
            where  
				product_id=productid
				and datediff(now(),group_enddate)<=0
				and group_current<group_max
                and group_refund=0 
			order by group_current desc
            limit 2;
            
        end if;
    end if;

	#将推荐的商品选出来
	
    if recommend=1 then
		/*select 
				pd.id,
				pd.title,
				pd.inpromotion,		# 商品是否促销
				pd.promotion,		# 商品当前的促销模式（1，正常  2：团购  4：秒杀   8：预售）
				truncate(pd.lowprice,2) as cost,
                pd.cover,
				pd.describ,
				pd.lowprice,
				pd.maxprice,
				pd.buycount			as salecount  #当前总销量
		from bus_product_recommend  rec 
		inner join vw_product pd on rec.recommend_product=pd.id
		where  rec.product_id=productid;
    end if;*/
    
		call proc_mall_recommendproducts(appid,productid);
	end if;

END ;;
DELIMITER ;
