CREATE DEFINER=`root`@`%` PROCEDURE `proc_mall_movepromotion2history`(
productid		char(36),
promotiontype	int,		#产品的促销类型
startdate datetime,
enddate datetime,
price_type tinyint,
price decimal,
pmin int,
limit_type int,
plimit int,
memo varchar(255)
)
BEGIN
	
	#将商品之前的促销移入至历史
    insert into bus_product_promotion_history(
		product_id,
        promotion_id,
        promotion_type,
        promotion_start,
        promotion_end,
		promotion_price_type,
		promotion_price,
        promotion_min,
        promotion_minute,
        promotion_limit_type,
        promotion_limit,
        promotion_memo)
	select 
		bpp.product_id,
        promotion_id,
        bp.product_type,
        promotion_start,
        promotion_end,
		promotion_price_type,
		promotion_price,
        promotion_min,
        promotion_minute,
        promotion_limit_type,
        promotion_limit,
        promotion_memo
	from bus_product_promotion bpp
    inner join bus_products bp on  bpp.product_id = bp.product_id
    where bpp.product_id = productid;
    
    #修改产品的形态
    update bus_products set product_type = pdtype where product_id=productid;
    
    if exists(select 1 from bus_product_promotion where product_id=productid) then
		update bus_product_promotion
		set promotion_start 		= startdate,
			promotion_end 			= enddate,
            promotion_price_type	= price_type,
            promotion_price			= price,
            promotion_min			= pmin,
            promotion_limit_type	= limit_type,
            promotion_limit			=	plimit,
            promotion_memo			= memo
		where  product_id=productid;
    else
		insert into bus_product_promotion(
		product_id,
        promotion_start,
        promotion_end,
		promotion_price_type,
		promotion_price,
        promotion_min,
        promotion_limit_type,
        promotion_limit,
        promotion_memo)
		values(
		product_id,
        
        startdate,
        enddate,
		price_type,
		price,
        pmin,
        limit_type,
        plimit,
        memo);
    end if;
    
    
END ;;
