CREATE DEFINER=`root`@`%` PROCEDURE `proc_mall_orderpayed`(
#支付完成
order_tx_no varchar(32),	#微信支付回调的交易订单号
paytype	tinyint				#支付的方式	0：微信现金支付   1：钱包余额支付
#paypassword	varchar(128)	#如果是余额支付,验证支付密码
)
outBusiness:BEGIN
	declare result 		int default 1;
    declare buyername	varchar(64);
	declare orderid 	char(36);
    declare appid		varchar(20);
    declare ordertype 	int;		#订单类型
    declare paytotal	decimal(8,2); #订单需要支付的金额
    declare grpid		int;		#订单的团购id
    declare voucher		int;		#用户的优惠券
    declare userid 		char(36);	#订单的用户
    declare productids	varchar(1024); #产品编号
    declare productname	varchar(1024); #产品名称
    declare groupsuccess int;		#团购订单是否成功
    declare groupmax	int;		#团购需要的成团人数
    declare groupendsec	int;		#距离团购结束剩余的秒
    declare groupusers	varchar(512);	#参与团购的用户id
    declare groupusernames	varchar(512);	#参与团购的用户姓名
   
    declare rebateusers	varchar(128);	#分享获益的用户，用于发送消息
    declare rebateusernames	varchar(128);	#分享获益的用户姓名，用于发送消息
    declare rebatemoneys	varchar(128);	#分享获益的用户获得的金额，用于发送消息
    declare notifymobile	varchar(128);	#需要通知的管理员（短信发送）
    
    select 
			order_id,bus_order.app_id,order_type,pay_total,groupid,user_voucher,bus_order.user_id,product_name,bus_order.product_id,user_name
            into orderid,appid,ordertype,paytotal,grpid,voucher,userid,productname,productids,buyername
	from bus_order 
    inner join crm_user u on bus_order.user_id = u.user_id
	left join bus_products bp on bus_order.product_id=bp.product_id
	where pay_tx_no = order_tx_no and order_status=0;
    
   
    
	
	#如果存在更新的影响的记录
    if orderid is not null then
		
    
		#修改订单状态，置位已支付
		update bus_order
			set 
				order_status	=1,
				pay_date		= now(),
				pay_type		=paytype,
				pay_valid_date 	= null
		where pay_tx_no = order_tx_no;
			
		set result = 0;
		set sql_safe_updates=0;
		
        
        
        #如果支付类型是余额支付，需要扣减用户的余额
        if paytype=1 then
			call proc_wallet_record(userid,0,1,orderid,paytotal);
		end if;
        
        #如果是团购订单，则累计拼团链接
        if ordertype=2 and grpid>0 then
			set groupsuccess = fun_increasegroupbuy(grpid,orderid);
            #if groupsuccess=1 then
				 select 
					group_concat(bus_order.user_id order by order_date separator ','),
                    group_concat(user_name order by order_date separator ',')
					into groupusers,groupusernames
					from bus_order 
                    inner join crm_user u on bus_order.user_id = u.user_id
                    where bus_order.groupid=grpid;
			#如果团购没有成功，则需要团购成功的人数
			if groupsuccess<>1 then
				select group_max,timestampdiff(second,now(),group_enddate)
                into groupmax,groupendsec
                from bus_grouplink 
                where group_id=grpid;
			end if;
            #end if;
        end if;
        #如果该订单中用户使用了优惠券，则需要将此优惠券置为已用状态
        #同时将未支付的订单中涉及此优惠券的都去除
        if ordertype=1 and voucher is not null then
			update crm_user_voucher set already_used = 1 where rec_id=voucher;
            #更新其他未支付的订单
            update bus_order 
				set user_voucher 	= null,
					voucher_total 	= null,
                    pay_total		= order_total + ifnull(logistics_total,0)	#待支付金额也要更改
			where user_id = userid and user_voucher = voucher and order_id<>orderid and order_status<>1;
        end if;
        #计算订单分销分成,只有常规订单可进行分成
        if ordertype=1 then
			call proc_mall_rebate4order(orderid);
            #获得分销收益的所有用户
            select 
				group_concat(rebate_user order by rec_id separator ','),
                group_concat(user_name order by rec_id separator ','),
                group_concat(rebate_money order by rec_id separator ',')
                into rebateusers,rebateusernames,rebatemoneys
            from bus_order_rebate r
            inner join crm_user u on r.rebate_user = u.user_id
            where order_id = orderid
            group by order_id;
		end if;
        #从订单明细中
        if productids is null then
			select 
				group_concat(product_id order by item_id separator ',')
                into productids
            from bus_order_detail od
     #       inner join bus_product_sku sku on od.sku_id = sku.sku_id
            where order_id = orderid
            group by order_id;
        end if;
    end if;
    
    set notifymobile = fun_getappsetting(appid,'notifyadmin',null);
    select 
		result,   #结果
        orderid,
        buyername,
		ordertype, #订单类型
        productids as products,
        productname,#涉及的产品名称（团购用）
        groupsuccess,
        groupmax,
        groupendsec,
        grpid as groupid, #团购id
        #团购参与者名称（用与发消息）
        groupusers, #团购参与者  （用与发消息）
        groupusernames,
        #以下为获得收益的用户
        rebateusers,
        rebateusernames,
        rebatemoneys,
        format(paytotal,2) as paytotal,
        notifymobile;	#需要通知管理员
END ;;
DELIMITER ;
