CREATE DEFINER=`root`@`%` PROCEDURE `proc_mall_parseshoppingcart`(
#解析购物车的产品信息
appid	varchar(20),			#商户的appid
skuids varchar(1024),			#购物车中的skuid连接，通过逗号分开
itemcount	varchar(1024),
recommend	int			#是否显示推荐产品
)
BEGIN
	declare accordingProducts varchar(1024) default null;
	 #用户所选择的数量
    DROP TABLE IF EXISTS `tmp_count`; 
    create temporary table `tmp_count` (
		`idx` int not null,
        `count` int not null default 1
	);
    call proc_splitString(itemcount,',');
    insert into tmp_count 
    select idx_,val_ from tmp_split;


    call proc_splitString(skuids,',');
    
    DROP TABLE IF EXISTS `tmp_shoppingcart`; 
	create temporary table `tmp_shoppingcart` (
    select 
		tmp_split.idx_	as itemindex,
        bp.catalog_id 	as 	catalogid,
        bp.id	as 	productid,
        bp.title	as	productname,
        val_			as 	skuid,
        if(ifnull(sku.sku_image,'')='',bp.cover,sku_image) as image,
        tmp_count.`count`,
        if(bp.indiscount=0,
		   sku_price,
		   truncate(sku_price * discount / 100,2))
           
        as 	price,    #通过价格处理方式得出商品的最终零售价
        ifnull(sku_qty,0) as 	quantity,	#当前sku的库存
		sku_memo		as 	skumemo
    from tmp_split
    inner join tmp_count on tmp_split.idx_ = tmp_count.idx
    inner join bus_product_sku sku on tmp_split.val_ = sku.sku_id
    inner join vw_product bp on sku.product_id = bp.id
	where 
		 sku.rec_isdeleted=0
		#and bp.promotion=1
	);
    
    #从临时表中选出
    select * from tmp_shoppingcart;
    
    if recommend=1 then
				
	 #没有找到购物车的产品，则选择5个销量最好的产品作为推荐
		select 
		group_concat(productid order by itemindex separator ',')
		into accordingProducts
		from tmp_shoppingcart;
	
		call proc_mall_recommendproducts(appid,accordingProducts);
	end if;
END ;;
