CREATE DEFINER=`root`@`%` PROCEDURE `proc_mall_ready4order`(appid		varchar(20),			#商户的appid
userid 		char(36),				#当前购买的用户id
productid	char(36),				#产品的id，如果skulist仅有一个选择直接购买，则需要传此参数
									#用于计算产品是否有秒杀或团购的属性
groupid		int,					#用户所选择的团购通道id,默认为0
skulist	   	varchar(1024),			#当前所选择的商品sku，如果是单买或者购物车只选择一个
									#则skulist仅一个，多个以逗号分开,
itemcount 	varchar(100),			#对应sku参数，表示每个产品购物的数量									
buytype		tinyint)
BEGIN
	#临时的订单id
	declare orderid		char(36) default uuid();
	#订单总金额
	declare ordertotal	decimal(8,2);
    declare paytotal	decimal(8,2);
    #订单可使用的优惠券
    declare voucherid		int;
    declare vouchertitle	varchar(64);
    declare vouchermoney	decimal(8,2);
    declare vouchercount 	int default 0;
	#declare productid char(36);		#对应的产品id ，仅当buytype = 0 或者 1的时候
    declare ordertype 	int default 1;	#订单类型，对应：
										/*
											1普通购买订单
											2团购订单
											4秒杀订单
                                            8预售订单
										*/
	declare serviceorder	int default 0; #订单是否服务订单
    declare servicecount	int default 0; #如果是服务订单，则记录总服务的次数
    
	declare invalidExceptionTotal decimal(8,2) default 0;     #计算满减时，需要排除掉不支持当前满减的商品，如果排除后还能
													#大于满减金额规则，则扣除满减
	declare fulldiscount	decimal(8,2) default 0; #满减金额
	declare logiticsmoney	decimal(8,2) default 0; #运费
    declare freelogiticslimit decimal(8,2) default 0; #系统设置的运费门槛
    
    
    declare promotionid int ;
	declare pricetype int default 1;   #商品是否可以秒杀或团购,以及对应的团购或秒杀的价格类型 0：一口价 1：订单折扣
    declare realprice decimal(8,2) default 100;
	
    declare groupmax	int; 			#满团限制
    declare groupcount	int; 			#参团人数/包括预参的订单	
    declare groupsucccessed int;		#团购链是否已成功
    declare grouprefund	int;			#团购是否已经退款
    declare groupvalid 	datetime;		#团购的过期日期
    
    declare balancepassword tinyint;	#余额是否设置了密码
    declare userbalance decimal(8,2);	#用户的钱包余额
    declare usercredit decimal(8,2);	#用户的积分
    declare exchangerate	decimal(8,2); #积分抵扣人民币比率
    
    #select 'freelogiticslimit',freelogiticslimit;
    #如果用户单独购买了一个产品，则根据用户传入的skuid计算出来
    select product_id into productid
    from bus_product_sku 
    where sku_id=skulist limit 1;
    
    if ifnull(productid,'')<>'' then
		select 
			(case 
				when promotion=4 and inpromotion=1 then 4
                when promotion=2 and inpromotion=1 and buytype=2 then 2
                when promotion=8 and inpromotion=1 then 8
                else 1 end),
            if(inpromotion=1,promotionpricetype,1),
            if(inpromotion=1,promotionprice,100),
            isservice
		into ordertype,pricetype,realprice,serviceorder
		from vw_product
        where id = productid;
        
        #如果订单的类型是促销的类型，则获取到当前的促销id
        if ordertype<>1 then
			select promotion_id 
            into promotionid
            from bus_product_promotion
            where product_id = productid;
        end if;
    end if;
    
    #获取设置的运费门槛,产品如果非虚拟产品或服务，则需要计算物流费用
    if serviceorder = 0 then
		set freelogiticslimit = cast(fun_getappsetting(appid,'logiticsfree',0) as decimal(8,2));
    end if;
    
    #用户所选择的数量
    DROP TABLE IF EXISTS `tmp_count`; 
    create temporary table `tmp_count` (
		`idx` int not null,
        `count` int not null default 1
	);
    call proc_splitString(itemcount,',');
    insert into tmp_count 
    select idx_,val_ from tmp_split;

    #拆解用户所选择的sku
    DROP TABLE IF EXISTS `tmp_sku`; 
    create temporary table `tmp_sku` (
		`idx` int not null,
        `catalog_id` char(36) null,		#产品的类型
        `product_id` char(36) not null,		#产品的id
        `product_title` varchar(128) not null,	#产品名称
        `sku_id` int,						#skuid
        `image` varchar(255),				#图片
        `count` int not null default 1,		#购买的数量
        `times` int not null default 1,
        `price` decimal(8,2),				#价格
        `exception` tinyint,				#商品参与满减标记
        `sku_memo` varchar(64)				#sku描述
	);
    call proc_splitString(skulist,',');

    
    insert into tmp_sku 
    select 
		tmp_split.idx_,
        bp.catalog_id,
        bp.id,
        bp.title,
        val_,
        if(ifnull(sku.sku_image,'')='',bp.cover,sku.sku_image),
        tmp_count.`count`,
        ifnull(sku.sku_qty,0),
        case
			when ordertype<>1 then	#如果当前进入的购买直接是单一产品或者促销方式进入,则使用促销时的价格
				if (pricetype=0,realprice,sku_price *realprice / 100)     #通过价格处理方式得出商品的最终零售价
			when ordertype=1 then		#否则如果商品开通了限时折扣，就使用限时折扣价
				if (bp.indiscount=0,sku_price,sku_price*ifnull(bp.discount,100) / 100)
        end,
         0,
         sku_memo
    from tmp_split
    inner join tmp_count on tmp_split.idx_ = tmp_count.idx
    inner join bus_product_sku sku on tmp_split.val_ = sku.sku_id
    inner join vw_product bp on sku.product_id = bp.id
	where 
		 sku.rec_isdeleted=0;
        #and bp.rec_isdeleted=0;
 
    #计算订单总金额
    select sum(`count` * `price`),sum(`count`*`times`)
    into ordertotal,servicecount
    from tmp_sku;
    
    
    #计算排除在满减之外的产品的总金额
    if ordertype=1 then
		update 
			tmp_sku
		inner join bus_full_exception fe 
			on 
			(tmp_sku.catalog_id = fe.data_id and fe.data_type=0) or 
            (tmp_sku.product_id = fe.data_id and fe.data_type=1) 
        set exception = 1;
        #计算排除的这些商品的总价值
        select sum(`count` * `price`) 
			into invalidExceptionTotal
		from tmp_sku where exception=1;
    
    end if;
    
   
   #计算运费,如果订单金额低于免运费的门槛，则需要运费
   if ordertotal<freelogiticslimit then
		set logiticsmoney = cast(fun_getappsetting(appid,'logitics',0) as decimal(8,2));
   end if;

   #获取可以使用的优惠券张数，具体使用哪一张由用户自己去挑
   #仅普通的订单可以使用优惠券
   if ordertype=1 then 
		select 
			count(1) into vouchercount
		#	 uv.rec_id
        #    ,voucher_title
        #    ,voucher_money
		#into voucherid,vouchertitle,vouchermoney
        from crm_user_voucher uv
        inner join bus_voucher bv on uv.voucher_id = bv.voucher_id
        where 
				user_id=userid 
            and already_used=0
            and datediff(now(),bv.voucher_start)>=0
            and datediff(ifnull(bv.voucher_end,now()+1),now())>=0
            and 
				(
					voucher_limit=0   # 无门槛优惠券
					or limit_order_total >= ordertotal   #有门槛，必须消费满才可以用
                )		
			and bv.rec_isdeleted=0
		order by bv.voucher_money desc			#选一张面值最大的优惠券
        limit 1;
        
        #普通订单可以计算满减金额
        
        select full_sub_total into fulldiscount
        from bus_full_promotion
        where full_enabled=1 and full_order_total<=ordertotal-ifnull(invalidExceptionTotal,0)
        order by full_sub_total desc
        limit 1;
        
   end if;

   #如果是团购,并且用户是选择了其他人的团购链接，
   #则需要计算当前团购链接是否已满fulldiscount
   if ordertype=2 then
	if groupid is not null and groupid>0 then
		#检查当前团购的人数是否已满或者团购是否已过期
        select group_id,group_max,ifnull(group_current,0),group_enddate,group_successed,group_refund
				into groupid,groupmax,groupcount,groupvalid,groupsucccessed,grouprefund
				from bus_grouplink 
                where group_id=groupid 
					and product_id=productid 
                    and group_max>group_current
                    and group_refund=0 
                    and timestampdiff(minute,group_enddate,now())<0;
                
		#该团购已被置位成功或后台已经退款
		if groupid is not null then
			#set groupid = null;
			#预参团的订单数量
			select groupcount+ifnull(count(1),0) 
				into groupcount
				from bus_grouplink_booked 
				where group_id=groupid and 
					timestampdiff(minute,valid_date,now())<0;
			if groupcount>=groupmax then
				#or datediff(now(),groupvalid)>0 then
				set groupid = null;
			else
				#插入预拼团的队伍中,120秒内必须支付
                insert into bus_grouplink_booked(group_id,order_id,valid_date)
                values(groupid,orderid,timestampadd(second,120,now()));
			end if;
        end if;
    end if;
    #如果团购订单的groupid为空，则创建一个group出来
    if groupid is null then
		insert into bus_grouplink(
			user_id,
            product_id,
            group_date,
            group_enddate,
            group_max,
            group_current,
            group_price,
            group_pricetype)
        select 
			userid,
            productid,
            now(),
            if(groupminute>0,timestampadd(hour,groupminute,now()),promotionend),
            groupmin,
            0,
            promotionprice,
            promotionpricetype
        from vw_product where id = productid;
		set groupid = LAST_INSERT_ID();
    end if;
   end if;
   
   
   /*******将以上计算出来的信息插入临时订单中*************/
   
   set paytotal = ordertotal-ifnull(vouchermoney,0)-ifnull(fulldiscount,0);
   #物流费用不能排除
   set paytotal = if(paytotal<=0,0,paytotal)+logiticsmoney;
   -- 插入临时订单主表
   insert into bus_order_draft(order_id,order_type,order_isservice,service_count,user_id,group_id,product_id,promotion_id,order_total,full_discount,user_voucher,voucher_total,logistics_total,pay_total)
   values(orderid,ordertype,serviceorder,if(serviceorder=1,servicecount,0),userid,if(ordertype=2,groupid,null),productid,promotionid,ordertotal,fulldiscount,voucherid,vouchermoney,logiticsmoney,
			if(paytotal>0,paytotal,0));
   
   
   -- 插入临时订单的明细
   insert into bus_order_detail_draft(order_id,product_id,sku_id,product_name,sku_memo,item_image,order_count,unit_price,exceptfull)
   select orderid,product_id,sku_id,product_title,sku_memo,image,`count`,price,exception from tmp_sku order by idx;
   
   #获取到钱包余额alter
   #set userbalance = fun_getuserbalance(userid);
   
	select ifnull(user_balance,0),ifnull(user_credit,0),if(ifnull(user_wallet_password,'')='',0,1)
   into userbalance,usercredit,balancepassword
   from crm_user where user_id = userid;
   
   #获取到花瓣抵扣人民币的比率
   
   set exchangerate =  cast(fun_getappsetting(appid,'exchange',0) as decimal(8,2));
	/************   以下内容为返回到前端的数据 ****************/
	#订单基础的信息
   select 
		orderid,									#临时订单id
		ordertype,									#订单类型
        groupid,									#团购链条id
		ordertotal,   								#订单总金额
        vouchercount,								#可使用的优惠券数量
        serviceorder,								#是否服务订单
        servicecount,								#服务次数
        logiticsmoney,								#订单物流运费
        fulldiscount,								#订单满减金额
        3 as paytype,								#订单支持的支付方式： 3的二进制位11 ， 0x01 代表支持微信支付  0x10代表支持余额支付
		balancepassword,							#用户是否设置了余额密码
        userbalance as balance,						#用户当前的余额
        if (userbalance>=paytotal,1,0)	as balanceenough,	#余额是否足够
        #实际需要支付的金额
		if(paytotal>0,paytotal,0) as paytotal;			
   
   #获取用户的默认送货地址
   select
		concat(address_province,address_city,address_region) as city,
        address_detail		as detail,
        address_receiver	as receiver,
        address_mobile		as mobile
   from crm_user_address
   where user_id = userid
   order by address_default desc, address_id
   limit 1;
   
   
   
   #购买的商品清单
   select 
		sku_id 			as id,
        product_title	as title,
        image,
        `count`,
        `price`,
        `sku_memo` 		as skuinfo,
        exception
   from tmp_sku order by idx;
   
  #如果是参加别人的团购链，则选出团购链上的人
  if ordertype=2 and groupid is not null then
			select 
				user_name,
				user_logo
			from bus_order bo
			inner join crm_user cu on bo.user_id = cu.user_id
			where bo.order_status>=1 and order_type=5 and `groupid`=groupid
			order by order_date desc;
   end if;

END ;;
