CREATE DEFINER=`root`@`%` PROCEDURE `proc_mall_rebate4order`(
#商城订单返利
orderid char(36)
)
label:BEGIN
    declare ordertype 	int;
    declare userid 		char(36);
    declare channelid 	char(36);
    declare shareparents varchar(255);

    declare rebatetotal decimal(8,2) default null;  #订单的总返利金额;
	DECLARE t_error INTEGER DEFAULT 0;  
	DECLARE CONTINUE HANDLER FOR SQLEXCEPTION SET t_error=1;  
	#通过订单计算其中的产品分销返利
    
    #订单如果是团购订单，则不支持返利
   
    select order_type,user_id,channel_id
	into ordertype,userid,channelid
    from bus_order 
    where order_id=orderid limit 1;
    
    #订单类型为团购或秒杀，不支持分销
	if ordertype!=1 then
		LEAVE label;
    end if;
    #如果该订单时渠道引入，则应该返利到对应的渠道
    if channelid is not null then
		#获得渠道对应绑定的用户
        set shareparents = fun_getchanneluser(channelid);
    else
		set shareparents = fun_getshareparent(userid,3);
	end if;
	#只有当具备返利的用户，订单开始返利
    if length(shareparents)>0 then
		if channelid is not null then #如果是渠道返利，应该用渠道的返利设置
			insert into bus_order_rebate
			(
				order_id,
				rebate_date,
				rebate_user,
                rebate_channel,
				rebate_money,
				rebate_level
			)
			select
				orderid,
				shareparents,		-- 渠道绑定的用户
                channelid,
                if(c.share_type=0,#固定金额返利
					c.share_rate,
                    o.pay_total * ifnull(c.share_rate,0) / 100),# 返利的金额
				1 #级数固定为1
				from 
					bus_order o
				inner join bus_channel c on o.channel_id=c.channel_id
				where 
					order_id=orderid;
        else
			call proc_splitString(shareparents,','); 
            #select * from tmp_split;
			#选择订单中的详细产品支持返利的产品，进行分销返利
			insert into bus_order_rebate
			(
				order_id,
				product_id,
				rebate_date,
				rebate_user,
				rebate_money,
				rebate_level
			)
            select
				orderid,
				bus_order_detail.product_id,
                now(),
				val_,		-- 分销返利的用户

				(
					case rebate_type 
						when 0 then order_count * ifnull(if(idx_=1, rebate_rate1,if(idx_=2,rebate_rate2,rebate_rate3)),0)
						when 1 then 
							(
								order_count * unit_price *  
								ifnull(if(idx_=1, rebate_rate1,if(idx_=2,rebate_rate2,rebate_rate3)),0) / 100
							)
						end
				),					# 返利的金额
				idx_
				from 
					bus_order_detail ,
					tmp_split,
					bus_product_sharerebate
				#inner join bus_product_sharerebate r on product_id = r.product_id
				where 
					bus_order_detail.product_id=bus_product_sharerebate.product_id 
					and order_id=orderid
					and rebate_enabled=1 and ifnull(val_,'')<>'';
            /*
			select
				orderid,
				r.product_id,
				sharep.val_,		-- 分销返利的用户
				
				(
					case r.rebate_type 
						when 0 then d.order_count * ifnull(if(sharep.idx_=1, r.rebate_rate1,if(sharep.idx_=2,r.rebate_rate2,r.rebate_rate3)),0)
						when 1 then 
							(
								d.order_count * d.unit_price *  
								ifnull(if(sharep.idx_=1, r.rebate_rate1,if(sharep.idx_=2,r.rebate_rate2,r.rebate_rate3)),0) / 100
							)
						end
				),					# 返利的金额
				sharep.idx
				from 
					bus_order_detail d,
					tmp_split sharep
				inner join bus_product_sku sku on d.sku_id = sku.sku_id
				inner join bus_product_sharerebate r on sku.product_id = r.product_id
				where 
					order_id=orderid 
					and r.rebate_enabled=1;
			*/
		end if;
        
        #删除掉返利金额为0 的
        
        delete from bus_order_rebate 
        where order_id = orderid and rebate_money=0;
		#修改订单的返利金额
		select sum(rebate_money) into rebatetotal from bus_order_rebate where order_id=orderid;
		update bus_order set rebate_total = rebatetotal where order_id = orderid;
		
		# 如果返利自动审核，应该进入用户的钱包和余额
		
		insert into crm_user_rebate(
			user_id,
			rec_date,
			io_type,
			bus_type,
			bus_data_id,
			rec_total,
            ext_field1
			)
		select 
			rebate_user,
			now(),
			1,
			if(channelid is not null,53,51),
			order_id,
			rebate_money,
            channelid
		from bus_order_rebate
		where order_id=orderid;
		# 增加用户的余额
		update crm_user 
		inner join bus_order_rebate on crm_user.user_id=bus_order_rebate.rebate_user
		set user_rebate = ifnull(user_rebate,0)+bus_order_rebate.rebate_money
		where bus_order_rebate.order_id=orderid;
    end if;
    
END ;;
