CREATE DEFINER=`root`@`%` PROCEDURE `proc_mall_rechargeorderpayed`(
#购物卡订单支付完成
order_tx_no varchar(32)	#微信支付回调的交易订单号
)
BEGIN
	declare userid 		char(36);
    declare orderid 	char(36);
    declare ordertotal 	decimal(8,2);		#订单充值的金额
    
    select order_id,user_id,order_total into orderid,userid,ordertotal from bus_recharge_order where order_no = order_tx_no and order_status=0 limit 1;
    
    
    #确认该订单是没有支付过的，确保不要给用户添加两次余额
	if orderid is not null then
		#修改订单状态，置位已支付
		update bus_recharge_order
			set 
				order_status	=3,     #因为充值订单无需物流，所以直接将order_status=3，保持和商品订单一直，直接已签收
				pay_date 		= now()
		where order_no = order_tx_no;
        
        #新增用户余额流水
        update crm_user set user_balance = ifnull(user_balance,0)+ordertotal where user_id=userid;
        
        #新增用户的余额流水记录
        insert into crm_user_wallet(
			user_id,
			rec_date,
			io_type,
			bus_type,
			bus_data_id,
			rec_total
			)
		values
			(
				userid,
                now(),
                1,
                51,
                orderid,
                ordertotal
            );
	end if;
	#如果存在更新的影响的记录
    select 0 as result;   #结果
END ;;
