products varchar(1024)
)
BEGIN
	#分解开依据的产品
	call proc_splitString(products,',');
    
	(select
				0 as itemtype,
				pd.id,
				pd.title,
                pd.cover,
				pd.inpromotion,		# 商品是否促销
				pd.promotion,		# 商品当前的促销模式（1，正常  2：团购  4：秒杀   8：预售）
				truncate(pd.lowprice,2) as cost,
				pd.describ,
				pd.lowprice,
				pd.maxprice,
				pd.buycount			as salecount  #当前总销量
			from vw_product pd
				where app_id= appid
				order by buycount desc
                limit 6)
	
			union
			 #猜您喜欢的推荐商品
			 (select 
				1 as itemtype,
				pd.id,
				pd.title,
                pd.cover,
				pd.inpromotion,		# 商品是否促销
				pd.promotion,		# 商品当前的促销模式（1，正常  2：团购  4：秒杀   8：预售）
				truncate(pd.lowprice,2) as cost,
				pd.describ,
				pd.lowprice,
				pd.maxprice,
				pd.buycount			as salecount  #当前总销量
				from vw_product pd
				where id in (
					select  recommend_product
					from bus_product_recommend bpc
                    inner join tmp_split ts
                    on bpc.product_id = ts.val_
				)
				order by lowprice 
                limit 6) 
			order by itemtype desc
			limit 6;

END ;;
