CREATE DEFINER=`root`@`%` PROCEDURE `proc_mall_refundgroup`(grpid int)
BEGIN
	#修改团购链，状态为退款
		update bus_grouplink 
			set group_refund 	= 1,
				group_successed	= 0
		where group_id 		= grpid;
	#获取到该团购链条下的订单
    select
		#获取用户id和产品名称是用于发送消息
		user_id		as userid,
        bp.product_name	as productname,
		pay_tx_no 	as trade_no,
        #退款的金额必须包含物流费用，因为还没有发货
        pay_total+ifnull(logistics_total,0)	as total_fee,
        pay_type	as pay_type		#支付方式用于原路返回
	from bus_order
    inner join bus_products bp on bus_order.product_id = bp.product_id
    where 
    groupid = grpid 
    and order_status=1 
    and refund_status=0;
    
END ;;
