CREATE DEFINER=`root`@`%` PROCEDURE `proc_mall_refundorder`(tradeno varchar(32),
refundno varchar(32))
BEGIN
	declare userid char(36);
    declare username varchar(50);
    declare paytype	int default 0;
    declare refundtotal decimal(8,2); #需要退款的金额
    declare productname	varchar(128); #退款的产品
    declare orderid	char(36);
    declare result int default 1;
    select 
		order_id,
		bo.user_id,
        user_name,
        bp.product_name,
        pay_type,
        pay_total + ifnull(logistics_total,0)    #如果退款则需要将物流也推给别人
	into orderid,userid,username,productname,paytype,refundtotal
	from bus_order bo
    inner join crm_user cu on bo.user_id = cu.user_id
    inner join bus_products bp on bo.product_id = bp.product_id
    where
		order_type=2 and
		pay_tx_no = tradeno and 
        order_status<>100 and 
        refund_status<>1;
    
    
    if userid is not null then
		set result = 0;
		#修改订单的状态及退款编号
		update bus_order
			set 
			refund_tx_no 	= refundno,   	#退款编号
			order_status 	= 100,    		#退款状态
			group_success	= 0,
			refund_status 	= 1,
            refund_date		= now()
		where pay_tx_no = tradeno;
        
        #如果订单是余额支付的，则需要退回到用户的钱包里面
        if paytype = 1 then
			call proc_wallet_record(userid,1,53,orderid,refundtotal);
        end if;
	 end if;
     
     select result, userid,username,productname;
#"P-1540378177","R-1540398177"
END ;;
