CREATE DEFINER=`root`@`%` PROCEDURE `proc_mall_refundsingleorder`(
#执行单个商品订单的申请退款
orderid char(36),
userid char(36))
BEGIN

    declare txno	varchar(20);
    declare refundno varchar(20) default concat('P-',unix_timestamp(now()));
    declare paytype	int default 0;
    declare refundtotal decimal(8,2); #需要退款的金额
    declare result int default 1;
    select 
		order_id,
        pay_tx_no,
        pay_type,
        pay_total
        # + ifnull(logistics_total,0)    #如果退款则需要将物流也推给别人
	into orderid,txno,paytype,refundtotal
	from bus_order bo
    where
		order_type<>2 
        and order_id=orderid
        and user_id = userid
        and order_status<>100 
        and refund_status<>1;
    
    #订单确实存在，且状态是可以退款的
    if orderid is not null then
		set result = 0;
		#修改订单的状态及退款编号
		update bus_order
			set 
			refund_tx_no 	= refundno,   	#退款编号
			order_status 	= 100,    		#退款状态
			group_success	= 0,
			refund_status 	= 1,
            refund_date		= now()
		where order_id = orderid;
        
        #如果订单是余额支付的，则需要退回到用户的钱包里面
        if paytype = 1 then
			call proc_wallet_record(userid,1,53,orderid,refundtotal);
        end if;
	 end if;
     #返回订单退款的数据到前端，执行退款；
	select result,txno,paytype, userid,refundtotal,refundno;

END ;;
