appid		varchar(20),			#商户的appid
userid 		char(36),				#用户id
orderid		char(36)				#订单的id	
)
BEGIN
	declare productid	char(36);	#产品
	declare grpid		int;		#用户所选择的团购通道id,默认为0
	declare skulist	   	varchar(1024);			#当前所选择的商品sku，如果是单买或者购物车只选择一个
	declare itemcount 	varchar(100);			#对应sku参数，表示每个产品购物的数量									
	declare ordertype	tinyint;				
													# 1 : 直接从详情那边点击直接购买过来
													# 2 : 直接从详情那边选择的团购进入
													# 4 : 秒杀订单
	
	select groupid,order_type 
			into grpid,ordertype
    from bus_order
    where order_id=orderid;
    #选出原订单购买的产品明细
    select 
		   group_concat(sku_id order by item_id separator ','),
		   group_concat(order_count order by item_id separator ',') 
           into skulist,itemcount
	from bus_order_detail
    where order_id=orderid;
    #如果原订单是团购或秒杀的，则也需要传productid
	if ordertype=2 or ordertype=4 then
		select product_id 
        into productid
        from bus_product_sku where find_in_set(sku_id,skulist) limit 1;
    end if;
    #select orderid,skulist,itemcount,productid,grpid,ordertype;
    
    #调用预生成订单
    call proc_mall_ready4order(appid,userid,productid,grpid,skulist,itemcount,ordertype);
