CREATE DEFINER=`root`@`%` PROCEDURE `proc_mall_sortrecommendproduct`(
recid int,
direction tinyint,   -- 0:  向上    1：向下移动
out result int	
)
BEGIN
	declare parentid char(36);
	declare nextRecordIndex int;
    declare nextId int;
    declare currentIndex int;
    
    
    select product_id, rec_index into parentid,currentIndex from bus_product_recommend where rec_id=recid;
    
    SET result = 0;
	-- 向上移动
	if direction = 0 then
		-- 找到上一条记录
        select rec_id,rec_index into nextId,nextRecordIndex from bus_product_recommend
        where rec_index < currentIndex and product_id=parentid order by rec_index desc limit 1;
    else -- 向下移动
		-- 找到下一条记录
        select rec_id,rec_index into nextId,nextRecordIndex from bus_product_recommend
        where rec_index > currentIndex order by rec_index limit 1;
    end if;
    
    -- 修改自己和待移动的字段位置
    if nextId is not null then
		update bus_product_recommend set rec_index=nextRecordIndex where rec_id=recid;
        update bus_product_recommend set rec_index=currentIndex where rec_id=nextId;
        SET result = 1;
    end if;
    SELECT result;
END ;;
DELIMITER ;
