CREATE DEFINER=`root`@`%` PROCEDURE `proc_mall_updateproductspec`(
	specid int,
    specname varchar(64),
    specvalue varchar(255),
    out result int)
BEGIN
	
	
    declare cnt int default 0;   
	declare i int default 0;   
    declare valueItem varchar(30);
    declare valueid int;
    declare productid char(36);
    declare oldspecvalue varchar(255);
    #启动事务保护
	DECLARE t_error INTEGER DEFAULT 0;  
	DECLARE CONTINUE HANDLER FOR SQLEXCEPTION SET t_error=1;  
    
    
    #从规格表里面选择出旧的记录对比，如果值没有发生变化
    #则不需要生成SKU
    select product_id,spec_value into productid,oldspecvalue 
    from bus_specification where spec_id = specid;
    
    
    #启动事务保护
	START TRANSACTION;  
    # 修改规格主表记录
    
    update bus_specification 
    set spec_name =specname, spec_value=specvalue
    where spec_id = specid;
    
    if oldspecvalue !=specvalue then
		
		# 统一将规格的所有选项设置为rec_isdeleted
		SET SQL_SAFE_UPDATES=0;
		update bus_specification_values set rec_isdeleted=1 where spec_id=specid;
		#逐一遍历规格的值，如果规格值已经存在的，
		#则只需要修改删除状态，否则新增一个值
		set cnt = fun_get_splitStringTotal(specvalue,',');   
		while i < cnt  do   
				set i = i + 1;   
				set valueItem = fun_splitString(specvalue,',',i);
				select value_id into valueid from bus_specification_values where spec_id=specid and value_title=valueItem;
				if valueid is null then
					insert into bus_specification_values(spec_id,value_title)values(specid,valueItem);
				else
					update bus_specification_values set rec_isdeleted=0  where value_id=valueid;
				end if;   
				SET valueid = null;
		end while;
        
        #重新生成SKU
        call proc_mall_generatesku(productid);
	end if;
    
    #事务提交
	IF t_error = 1 THEN  
		rollback;  
	ELSE  
        commit;  
	END IF;  
    set result=t_error;
    select result;
END ;;
DELIMITER ;
