CREATE DEFINER=`root`@`%` PROCEDURE `proc_message_scheduledMessage2Queue`(
	# 将定时或后台的消息
    # 目前到期的推送到消息队列中，准备发送
)
BEGIN
	declare varPushid int;
	declare varMessage varchar(50);
    declare varMethod tinyint default 0;
	declare varUrl varchar(255);
    declare varParam varchar(1024);
    declare varFilter varchar(1024);
    declare v_sql varchar(500) default '';
    declare pushidlist varchar(255) default '';
    declare varAllPushId varchar(255) default '';
	declare done int default 0;
    DECLARE t_error INTEGER DEFAULT 0;  
	declare cur CURSOR FOR 
    select push_id,push_message,push_method,ifnull(push_pageurl,''),
    ifnull(push_params,''),ifnull(push_sql_filter,'')
	from message_push_policy mpm
	where 
	push_users=1
	and rec_isdeleted=0 
	and push_status=0
	and (push_type=1 and push_date<=now());
   
	declare CONTINUE HANDLER FOR NOT FOUND SET done = 1;
    
	declare CONTINUE HANDLER FOR SQLEXCEPTION SET t_error=1;  
    
	OPEN cur;
    # 通过游标方式，获取自定义筛选的用户消息，放入到消息队列中
	read_loop: LOOP
		-- 提取游标里的数据，这里只有一个，多个的话也一样；
		FETCH cur INTO varPushid,varMessage,varMethod,varUrl,varParam,varFilter;
        IF done=1 THEN
		  LEAVE read_loop;
		END IF;
		-- 将每个push的用户记录插入的对列表中
		if ifnull(varFilter,'')!='' then
			set varFilter = concat(' where ',varFilter);
        end if;
		
		set v_sql= concat('insert into message_queue(user_id,templateid,push_type,pageurl,msg_param) ',
						  'select user_id,\'',varMessage,'\',',varMethod,',\'',varUrl,'\',\'',varParam,'\'',
                          ' from crm_user ',varFilter);
		#select v_sql;
		set @v_sql=v_sql;   -- 注意很重要，将连成成的字符串赋值给一个变量（可以之前没有定义，但要以@开头）
		      
		prepare stmt from @v_sql;  -- 预处理需要执行的动态SQL，其中stmt是一个变量
		execute	 stmt;      -- 执行SQL语句
         #select t_error;
		if t_error=0 then
			set varAllPushId = concat_ws(',',varAllPushId,varPushid);
        end if;
		deallocate prepare stmt;     -- 释放掉预处理段        
	  END LOOP;
	#set v_sql='se';
	  -- 关闭游标
	CLOSE cur;

	#将达到发送时间的消息，指定用户的推送到消息队列中
	select group_concat(push_id)  
    into pushidlist
    from message_push_policy mpm
	where 
	push_users=0
	and rec_isdeleted=0 
	and push_status=0
	and (push_type=1 and push_date<=now());
	
    insert into message_queue
			(user_id,templateid,push_type,pageurl,msg_param)
	select mpu.user_id,push_message,push_method,push_pageurl,push_params
	from message_push_user mpu
	inner join message_push_policy mpm  on mpu.push_id = mpm.push_id
	where
	find_in_set(mpm.push_id,pushidlist);
    
    # select t_error;
     
     if t_error=0 then
		set varAllPushId = concat_ws(',',varAllPushId,pushidlist);
        -- update message_push_policy set push_status=1 where find_in_set(push_id,varAllPushId);
    end if;
    #select varAllPushId;
     update message_push_policy set push_status=1,push_act_date=now() where find_in_set(push_id,varAllPushId);
    
END ;;
DELIMITER ;
