CREATE DEFINER=`root`@`%` PROCEDURE `proc_rebate_widthdrawover`(
	#更新收益的转账记录状态
    orderno varchar(20),
    successed tinyint,
    errorcode varchar(32)
)
BEGIN
	declare userid char(36);
    declare appmoney decimal(8,2);
    declare orderid char(36);
    
    #防止重复调用
    if exists(select 1 from pf_rebate_draw where order_no=orderno and  transfer_state=0) then
    
		if successed=1 then	  #提现转账成功

			#修改记录
			update pf_rebate_draw 
				set transfer_state=1,
					transfer_date=now() 
			where order_no=orderno;

		else     # 提现转账失败

			#修改记录
			update pf_rebate_draw 
				set transfer_state=100,
					transfer_date=now(),
					ext_field1=errorcode
			where order_no=orderno;
			#失败需要将申请的金额转入用户的收益余额
			select draw_id,user_id,draw_money into orderid,userid,appmoney
			from pf_rebate_draw where order_no=orderno;
			call proc_rebate_record(userid,1,52,orderid,appmoney);
		end if;

	end if;

END ;;
