CREATE DEFINER=`root`@`%` PROCEDURE `proc_rebate_withdraw`(
	#用户申请余额提现
	userid char(36),    #申请的用户
	money decimal(8,2),  #提现的金额
	appid varchar(36),	#申请的appid
	openid varchar(36)  #申请用户的openid
)
BEGIN

	declare result int default 99;
	declare orderno varchar(20);
    declare msg varchar(64) default '余额不足';

    declare orderid char(36) default uuid();

    

    DECLARE t_error INTEGER DEFAULT 0;  

    DECLARE CONTINUE HANDLER FOR SQLEXCEPTION SET t_error=1;

    #判定用户的余额是否足够提现金额

    if exists(select 1 from crm_user where ifnull(user_rebate,0)>=money and user_id=userid) then

		set orderno = concat('W',unix_timestamp(now())); #生成提现的订单号 #fun_generate_orderno(-1);  

        START TRANSACTION;  #启动事务保护

        #插入提现记录申请

        insert into pf_rebate_draw(draw_id,order_no,user_id,draw_date,draw_money,app_id,open_id)

        values(orderid,orderno,userid,now(),money,appid,openid);

        IF t_error = 0 THEN 
			#增加收益流水表的流水记录
			#扣减用户的余额
            call proc_rebate_record(userid,0,1,orderid,money);
		end if;
        set result=t_error;
        #提交事务
        IF t_error = 1 THEN
			set msg = '提现失败,数据库错误';
			ROLLBACK;  
		ELSE  
			set msg = null;
			COMMIT;  
		END IF;
    end if;

    select result,orderno,orderid,msg;

END ;;
