CREATE DEFINER=`root`@`%` PROCEDURE `proc_redbag_alloc`(
#为需求分配红包
redbagid int,  #红包id
bonustotal decimal(8,2),    #红包总金额
bounscount int,      #红包数量
alloctype int			#分配方式
)
BEGIN
    declare remainTotal int;
    declare remainCnt int;
    declare bonus int ;
    declare bonusSql varchar(5000) default '';
    declare avgamount int;
    declare reqStr varchar(10) default  concat('\'',redbagid,'\'');
	if bonustotal>0 then
            set remainCnt	= bounscount;
			set remainTotal = bonustotal * 100;
            set avgamount 	= bonustotal * 100 / bounscount;
            repeat 
				set bonus 	= if(alloctype=0,func_randAllocBouns(remainTotal,remainCnt),avgamount);
                set remainTotal = remainTotal-bonus;
                #select remainTotal;
                set bonusSql = concat_ws(',',bonusSql,  concat('(',reqStr,',',cast((bonus/100) as decimal(6,2)),')'));
				set remainCnt = remainCnt-1;
                #到一定长度就需要执行
                if length(bonusSql)>3000 then 
					set bonusSql=substring(bonusSql,2);
					set @exeSql=concat("insert into pf_redbag_alloc(redbag_id,alloc_money) values ",bonusSql);  
						-- select @exeSql;  
						prepare stmt from @exeSql;  
						EXECUTE stmt;  
						deallocate prepare stmt;  
					set bonusSql='';
				end if;
			until remainCnt <=0
			end repeat;
		if length(bonusSql)>0 then 
			set bonusSql=substring(bonusSql,2);
			set @exeSql=concat("insert into pf_redbag_alloc(redbag_id,alloc_money) values ",bonusSql);  
				-- select @exeSql;  
				prepare stmt from @exeSql;  
				EXECUTE stmt;  
				deallocate prepare stmt;  
		end if;
    end if;
END ;;
