	bagid int,
	userid char(36),
    shareKey varchar(20)     #必须要有分享口令
)
business:BEGIN
	#首先确认用户是否已经领取过红包
    declare money decimal(8,2);
    declare allocid int;
    declare result int default 0;
	DECLARE t_error INTEGER DEFAULT 0;  
    DECLARE CONTINUE HANDLER FOR SQLEXCEPTION SET t_error=1;
    
    select alloc_money into money  from pf_redbag_alloc 
    where user_id=userid and redbag_id=bagid limit 1;
    if money is not null then
		select 100 as result,money;
        leave business;
    end if;
    #用户没有传分享口令，返回让用户分享
    if shareKey<>'topTeaChina' then
		select 98 as result,money;
        leave business;
    end if;
     #从红包里面获取记录
    set autocommit=0;
    start transaction;
        select alloc_id,alloc_money into allocid,money
        from pf_redbag_alloc 
			where redbag_id=bagid 
			and user_id is null
			order by alloc_id
			limit 1
        for update;
        #select allocid,money;
		if allocid is not null then
			update pf_redbag_alloc set user_id =userid,gotdate=now() where alloc_id=allocid;
			#更新用户余额
			 #更新用户的钱包余额
			if t_error=0 and ifnull(money,0)>0 then
				update crm_user set user_balance = ifnull(user_balance,0)+money where user_id=userid;
			end if;
            #添加用户钱包流水记录
            if t_error=0 and ifnull(money,0)>0 then
				insert into crm_user_wallet(user_id,rec_date,io_type,bus_type,bus_data_id,rec_total)
                values (userid,now(),1,55,allocid,money);
				
			end if;
		else
			set result = 99;   #已经没有红包了
		end if;
        
        if t_error=0 then
			commit;
		else
			set result=1;
			rollback;
		end if;
		
		select result, money;
END ;;
