CREATE DEFINER=`root`@`%` PROCEDURE `proc_splitString`(IN f_string varchar(1000),IN f_delimiter varchar(5))
BEGIN   
declare cnt int default 0;   
declare i int default 0;
declare tmpIndex int default 0;
declare tmp varchar(64);
set cnt = fun_get_splitStringTotal(f_string,f_delimiter);   

DROP TABLE IF EXISTS `tmp_split`; 
create temporary table `tmp_split` (`val_` varchar(128) not null,`idx_` int) DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;   
while i < cnt   
do   
set i = i + 1;  
set tmp = fun_splitString(f_string,f_delimiter,i);
if ifnull(tmp,'')<>'' then
	set tmpIndex=tmpIndex+1; 
	insert into tmp_split(`val_`,`idx_`) values (tmp,tmpIndex);  
end if; 
end while; 


END ;;
