CREATE DEFINER=`root`@`%` PROCEDURE `proc_UI_getLayout`()
BEGIN
	select 
		tab_id,
        tab_title,
        tab_index,
        tab_content_type,
        tab_url
	
    from ui_tablayout where tab_visible=1  and rec_isdeleted=0
	order by tab_index;

	#
	#	0:广告推广位(Slider)
	#	1:产品分类展示
	#	2:分隔图
	#	3:纯html

	select 
		tabs.tab_id,
        tabs.tab_url,
        items.item_id,
        item_index,
        item_type,
        case item_type 
			when 0 then 'slider'
            when 1 then 'product'
            when 2 then 'image'
            when 3 then 'html'
            else 'other'
            end  as ttype,
        item_product_template,
        item_image,
        item_image_click,
        item_image_param,
        slider_id,
        slider_image,
        product_id,
        show_image
    from ui_tablayout tabs 
	left join ui_tab_items items  on tabs.tab_id=items.tab_id
	left join ui_tabitem_slider slider on slider.item_id=items.item_id
	left join ui_tabitem_products products on products.item_id=items.item_id
	where tabs.rec_isdeleted=0   and items.rec_isdeleted=0
	order by items.tab_id,item_index;
END ;;
