					  when 3 then item_html  
					  when 4 then item_video
					  when 5 then '' end) as param,  
		item_index as orderindex,
        #2 as level,
		item_image_click 	as ext1,
        item_image_pdtype	as producttype
	from ui_tab_items 
	where  
		item_id=itemid;
	 
    #slider
	select 
		slider_id 			as id,
		item_id 			as pid,
        #'' as title,
        slider_click 		as type,
		slider_image 		as image,
		slider_click_param 	as param,
        slider_product_type	as producttype
		from ui_tabitem_slider
	where  item_id=itemid 
		and slider_visible=1
	order by slider_index;

    #label
	select 
		label_id 			as id,
        item_id 			as pid,
        label_text 			as title,
        label_click 		as type,
		label_icon 			as image,
		label_click_param 	as param
       
		from ui_tabitem_label
	where  
		item_id=itemid 
        and label_visible=1
	order by label_index;
	
    
    #product
	select 
		rec_id 				as id,
        item_id 			as pid,
        pd.title,
		ifnull(uipd.show_image,pd.cover) 
							as image, 
		uipd.product_id 	as param,
        pd.promotion,
		pd.promotionstart,
		pd.promotionend,
        pd.promotionstartdistance,
        pd.promotionenddistance,
        pd.promotionlimit,
		FUN_JOIN2PRICE(promotion_low,promotion_max,NULL) AS promotioncost,
		pd.describ,
		pd.lowprice,
		pd.maxprice,
		pd.buycount			as salecount  #当前总销量
		from ui_tabitem_products uipd
        inner join tmp_item on uipd.item_id=tmp_item.id
        inner join vw_product pd on uipd.product_id = pd.id
		where   product_visible=1            
		order by product_index;
 
END ;;
