CREATE DEFINER=`root`@`%` PROCEDURE `proc_UI_moveLabel`(
labelid int,
direction tinyint,   -- 0:  向上    1：向下移动
out result int	
)
BEGIN
	declare parentid char(36);
	declare nextRecordIndex int;
    declare nextId int;
    declare currentIndex int;
    
    
    select item_id, label_index into parentid,currentIndex from ui_tabitem_label where label_id=labelid;
    
    SET result = 0;
	-- 向上移动
	if direction = 0 then
		-- 找到上一条记录
        select label_id,label_index into nextId,nextRecordIndex from ui_tabitem_label
        where label_index < currentIndex and item_id=parentid order by label_index desc limit 1;
    else -- 向下移动
		-- 找到下一条记录
        select label_id,label_index into nextId,nextRecordIndex from ui_tabitem_label
        where label_index > currentIndex order by label_index limit 1;
    end if;
    
    -- 修改自己和待移动的字段位置
    if nextId is not null then
		update ui_tabitem_label set label_index=nextRecordIndex where label_id=labelid;
        update ui_tabitem_label set label_index=currentIndex where label_id=nextId;
        SET result = 1;
    end if;
    SELECT result;
END ;;
