CREATE DEFINER=`root`@`%` PROCEDURE `proc_user_getprofiledata`(
	#获取我的数据
	userid char(36)
)
BEGIN

	#个人基本信息（头像、名称、会员级别）
    select 
		 user_name	as name
		,user_logo	as logo
        ,user_mobile	as mobile	#用户绑定的手机号码
        ,format(ifnull(user_balance,0),2)	as balance   #用户的钱包余额
        ,format(ifnull(user_rebate,0),2)	as rebate    #用户的分销收益
        #收货地址
        ,(select count(1) from crm_user_address where user_id=userid) as addresscount
        #用户优惠券
        ,(select count(1) from crm_user_voucher where user_id=userid and already_used=0) as vouchercount
        #用户购物卡张数
        ,(select count(1) from crm_user_card where user_id=userid and card_status=0) as cardcount
	from 
		crm_user
	where user_id=userid;
    
    #获取订单的情况
    select 
		order_status,
        count(1) as ordercount
	from bus_order
    where 
		user_id=userid and 
		timestampdiff(second,ifnull(pay_valid_date,now()+1),now())<0   #任然可以支付
    group by order_status
    order by order_status;
END ;;
