CREATE DEFINER=`root`@`%` PROCEDURE `proc_voucher_center`(
#领券中心领取优惠券
	appid varchar(20),	#商户的id
	userid char(36)   #当前的用户id
)
BEGIN
	#检查用户是否新用户
	declare isnewer int;
    declare channelid char(36);
    set isnewer 	= 	fun_userisnewer(userid);
    set channelid	=	fun_userfromchannel(userid);    #获得用户来源的渠道
	 #将所有可领取的优惠券放入临时表
    DROP TABLE IF EXISTS `tmp_voucher`; 
    create temporary table `tmp_voucher` (
		SELECT 
			voucher_id 			as id,
			voucher_title		as title,
			voucher_start 		as startdate,
			voucher_end 		as enddate,
			voucher_money 		as money, 
			voucher_limit		as `limit`,
			limit_order_total	as `limittotal`,
            user_type			as usertype,
            repeat_type			as repeattype
			#voucher_count-(select count(1) from crm_user_voucher where voucher_id=v.voucher_id) as remaincount
		FROM bus_voucher v
		where
			app_id = appid and
            (v.channel_id is null or v.channel_id=channelid) and
			voucher_publish_type=0 and  #应用内领取
			datediff(voucher_end,now())>=0 and #没有过期
			datediff(now(),voucher_publish_start)>=0  and 
			datediff(now(),voucher_publish_end)<=0 and
            voucher_count>(select count(1) from crm_user_voucher where voucher_id=v.voucher_id) and 
            voucher_id not in (select voucher_id from crm_user_voucher where user_id=userid and already_used=0) and
            rec_isdeleted=0
            
	);
    
    select 
		id,
        title,
        date_format(startdate,'%Y年%m月%d日') as startdate,
		date_format(enddate,'%Y年%m月%d日') as enddate,
        money,
        `limit`,
        `limittotal`,
        usertype
    from tmp_voucher
    where  
	(usertype = 0 or (usertype=1 and isnewer=1) or (usertype=2 and isnewer=0))   #发放的用户对象
	and 
	((repeattype=1 and id not in (select voucher_id from crm_user_voucher where user_id=userid))
            or 
		repeattype=0) #重复领用设置
	order by enddate desc;   
    
END ;;
